var getConfigData = $node["get-config-data"].json;

var chunks = items[0].json.chunks;

var trainsInfo = getConfigData.repo.trains_info;

var tableFormat= `|Icon|Chart|Train|Description|\n| :-------------: |:-------------: | :-------------:	| :-------------	|`;

var markDownData = [];

function capitalizeFirstLetter(string) {
  return string.charAt(0).toUpperCase() + string.slice(1);
}

function getTrainInfo(train){
  let trainInfo = trainsInfo[train];
  return trainInfo != null || undefined ? trainInfo: trainsInfo.wip;
}

function keyGen(){
  let keyDescription = "Train Descriptions:\n";
  let trainsList = Object.keys(trainsInfo);

  for (train in trainsList){
    var d = trainsInfo[trainsList[train]];
    keyDescription += `\n- ${d.emoji} = ${capitalizeFirstLetter(d.name)} Train -> ${d.description}`
  }
  return keyDescription + "\n\n";
}

