var list = [];

function containsAny(str, substrings) {
    for (var i = 0; i != substrings.length; i++) {
       var substring = substrings[i];
       if (str.indexOf(substring) != - 1) {
         return substring;
       }
    }
    return null; 
}

function isNew(date){
  var maxDays = getConfigData.max_days_new_badge;
  let d1 = new Date(date);
  return +new Date() < d1.setDate(d1.getDate() + (maxDays||0))
}

