var filteredList = [];

function containsAny(str, substrings) {
    for (var i = 0; i != substrings.length; i++) {
       var substring = substrings[i];
       if (str.indexOf(substring) != - 1) {
         return substring;
       }
    }
    return null; 
}

var serverChunks = [];

for(server in items){
  var data = items[server].json;
  var list = data.list;
  var jsonFile = data.jsonFile;
  var serverDescription = data.serverDescription

  if(fList.length != 0){
  filteredList = list.filter(chart => !containsAny(chart.appName, fList));
  } else {
    filteredList = list;
    }

  var sort_app_list = filteredList.sort(function (a, b) {
    return a.serverName.localeCompare(b.serverName) || a.chartName.localeCompare(b.chartName);
});
  var removeDupeList = sort_app_list.filter((value, index, self) =>
  index === self.findIndex((t) => (
    t.chartName === value.chartName
  )));

  var countChunkList = removeDupeList.reduce((accumulator, object) => {
  return accumulator + object.instances;
}, 0);
  serverChunks.push({serverName: data.serverName, serverDescription: serverDescription, count: countChunkList, jsonFile: jsonFile, list: removeDupeList})// sort_app_list
}
var sortServerChunks = serverChunks.sort(function (a, b) {
    return a.serverName.localeCompare(b.serverName);
});
return {chunks: [...sortServerChunks]};

